mapper.insert:
/*	designReport.insertDesign */
INSERT INTO lab_design(
	DESIGN_IDX
	, PRODUCT_CODE
	, SAP_CODE
	, TITLE
	, PRODUCT_NAME
	, CONTENTS
	, VERSION_NO
	, IS_DELETE
	, IS_LAST
	, STATUS
	, REG_USER
	, DOC_OWNER			
)VALUES(
	#{idx}
	, #{productCode}
	, #{sapCode}
	, #{title}
	, #{productName}
	, #{contents}
	, 1
	, 'N'
	, 'Y'
	, #{status}
	, #{userId}
	, #{userId}			
)

mapper.update:
/*	designReport.updateDesign */
	UPDATE 
		lab_design
	SET 
		PRODUCT_CODE = #{productCode}
		, SAP_CODE = #{sapCode}
		, TITLE = #{title}
		, PRODUCT_NAME = #{productName}
		, CHANGE_COMMENT = #{changeComment}
		, CHANGE_TIME = #{changeTime}
		, CONTENTS = #{contents}
		
		, STATUS = #{status}
		
	WHERE 1 = 1
		AND DESIGN_IDX = #{idx}

mapper.delete:
/*	designReport.deleteChangeList */
	DELETE
	FROM lab_design_change_info
	WHERE 1 = 1
		AND DESIGN_IDX = #{idx}
