mapper.select:
/* user.userCount 리스트 카운트 */
		SELECT	COUNT(*)
		FROM	lab_user	
		WHERE 1=1
			AND EMSTAT = 1
		
			AND (USER_NAME like  concat('%',#{searchValue},'%') OR USER_ID like  concat('%',#{searchValue},'%')  OR EMAIL like  concat('%',#{searchValue},'%') )
		
		
			AND DEPT_CODE = #{deptCode}
		
		
			AND TEAM_CODE = #{teamCode}
		
		
			AND USER_GRAD = #{userGrade}

/* user.userList 리스트 */ SELECT t.* , (select OBJTTX from lab_interface_hr1010 hr1010 where hr1010.OBJTID = t.ORGAID ) as OBJTTX , (select CDTEXT from lab_interface_hr9001 hr9001 where hr9001.CDVALU = t.TITLCD and hr9001.CDTYPE = 'TITLCD') as TITL_TXT , (select CDTEXT from lab_interface_hr9001 hr9001 where hr9001.CDVALU = t.LEVLCD and hr9001.CDTYPE = 'LEVLCD') as LEVEL_TXT , (select CDTEXT from lab_interface_hr9001 hr9001 where hr9001.CDVALU = t.RESPCD and hr9001.CDTYPE = 'RESPCD') as RESP_TXT , (select TOBJID from lab_interface_hr1010 hr1010 where hr1010.OBJTID = t.ORGAID ) as TOBJID FROM ( SELECT row_number() over (order by USER_ID desc) as rnum ,USER_ID AS userId ,USER_PWD AS userPwd ,USER_NAME AS userName ,USER_GRAD AS userGrade ,getCodeName('GRADE',USER_GRAD) AS userGradeName ,DATE_FORMAT(REG_DATE,'%Y-%m-%d') AS regDate ,USER_CODE AS userCode ,EMAIL AS email ,DEPT_CODE AS deptCode ,getCodeName('DEPT',DEPT_CODE) AS deptCodeName ,TEAM_CODE AS teamCode ,getCodeName('TEAM',TEAM_CODE) AS teamCodeName ,TITLE_CODE AS titleCode ,USER_TYPE AS userType ,IS_ADMIN AS isAdmin ,IS_DELETE AS isDelete ,IS_LOCK AS isLock , ORGAID , TITLCD , LEVLCD , RESPCD , EMSTAT FROM lab_user WHERE 1=1 AND EMSTAT = 1
AND (USER_NAME like concat('%',#{searchValue},'%') OR USER_ID like concat('%',#{searchValue},'%') OR EMAIL like concat('%',#{searchValue},'%') )
AND DEPT_CODE = #{deptCode}
AND TEAM_CODE = #{teamCode}
AND USER_GRAD = #{userGrade}
) t WHERE rnum between #{startRow} and #{endRow}
ORDER BY t.userId desc

mapper.insert:
/* user.insert 사용자 등록  */
INSERT INTO lab_user(
	USER_ID
	,USER_NAME
	,USER_GRAD
	,EMAIL
	,DEPT_CODE
	,TEAM_CODE
	,REG_DATE
	,IS_DELETE
	,  IS_ADMIN
	,MAIL_CHECK1
    ,MAIL_CHECK2
    ,MAIL_CHECK3
)	VALUES (
	#{userId}
	,#{userName}
	,#{userGrade}
	,#{email} 
	,#{deptCode}
	,#{teamCode}
	,sysdate()
	,'N'
	,#{isAdmin}
	,'Y'
	,'Y'
	,'Y'
)

mapper.update:
	/*	user.update 사용자 정보 조회 */
	UPDATE lab_user 
	SET 
      USER_NAME =  #{userName}
      ,USER_GRAD =  #{userGrade}
      ,EMAIL = #{email}
      ,DEPT_CODE =  #{deptCode}
      ,TEAM_CODE =  #{teamCode}
      ,IS_ADMIN	=  #{isAdmin}
	WHERE	1 = 1
		AND USER_ID = #{userId}

	/*	user.restore 사용자 정보 조회 */
	UPDATE lab_user 
	SET 
     IS_DELETE	=  'N'
	WHERE	1 = 1
		AND USER_ID = #{userId}

	/*	user.unlock 사용자 잠금해제  */
	UPDATE lab_user 
	SET 
     IS_LOCK	=  'N'
	WHERE	1 = 1
		AND USER_ID = #{userId}

