mapper.select:
/* user.selectUserMenu */ SELECT t.menuId , t.menuName , t.eMenuName , t.pMenuId , t.level , t.displayOrder , t.url FROM ( WITH RECURSIVE tmp AS ( SELECT t1.menuId , t1.pMenuId , t1.menuName , t1.menuName as pathnm , t1.eMenuName , t1.level , t1.displayOrder , t1.url , t1.useYn , t1.regUserId , DATE_FORMAT(t1.regDate,'%Y-%m-%d') as regDate FROM lab_system_menu t1 , (SELECT menu.pMenuId FROM lab_system_role_menu role_menu , lab_system_menu menu WHERE 1 = 1 AND role_menu.ROLE_IDX = #{roleCode} AND role_menu.MENU_IDX = menu.menuId group by menu.pMenuId ) role_menu WHERE 1 = 1 AND t1.pMenuId is null AND role_menu.pMenuId = t1.menuId and t1.useYn = 'Y' and t1.displayYn = 'Y'
UNION ALL
SELECT t2.menuId , t2.pMenuId , t2.menuName , concat(tmp.pathnm, ' > ', t2.menuName) as pathnm , t2.eMenuName , t2.level , t2.displayOrder , t2.url , t2.useYn , t2.regUserId , DATE_FORMAT(t2.regDate,'%Y-%m-%d') as regDate FROM lab_system_menu t2 INNER JOIN tmp ON t2.pMenuId = tmp.menuId INNER JOIN lab_system_role_menu lm ON t2.menuId = lm.MENU_IDX and lm.ROLE_IDX = #{roleCode} where t2.useYn = 'Y' and t2.displayYn = 'Y' ) SELECT * FROM tmp ORDER BY tmp.level, tmp.displayOrder )t

