mapper.insert:
/*	marketResearch.insertMarketResearch */
INSERT INTO lab_market_research(
	RESEARCH_IDX
	
	, PLAN_IDX
	
	, TITLE
	, TRIP_TYPE
	, RESEARCH_DATE
	, COST
	, REG_USER
	, DOC_OWNER
	, STATUS
	, IS_LAST
	, IS_DELETE
	, VERSION_NO
)VALUES(
	#{idx}
	
	, #{planIdx}
	
	, #{title}
	, #{tripType}
	, #{researchDate}
	, #{cost}
	, #{userId}
	, #{userId}
	, #{status}
	, 'Y'
	, 'N'
	, 1		
)

mapper.update:
/*	marketResearch.selectMarketResearchAddInfoList */
UPDATE 
	lab_market_research
SET
	TITLE = #{title}	
	
	, PLAN_IDX = #{planIdx}
	
	, TRIP_TYPE = = #{tripType}	
	, RESEARCH_DATE = #{researchDate}	
	, COST = #{cost}	
WHERE 1 = 1
	AND RESEARCH_IDX = #{idx}

mapper.delete:
/*	marketResearch.deleteMarketResearchAddInfo */
DELETE
FROM lab_market_research_add_info
WHERE 1 = 1
	AND RESEARCH_IDX = #{idx}

/*	marketResearch.deleteMarketResearchUser */
DELETE
FROM lab_market_research_user
WHERE 1 = 1
	AND RESEARCH_IDX = #{idx}
